package looping;

public class SwitchExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int number = 1;
    
    switch(number)
    {
    case 10 : System.out.println("The number entered is 10");break;
    case 1 : System.out.println("The number entered is 1");
    case 2 : System.out.println("The number entered is 2");
    case 30 : System.out.println("The number entered is 30");
    
    default:System.out.println("Its a different number");
    
    }
    		
    		
    		
	}

}
